const mongoose = require("mongoose");

const oLevelSchema = new mongoose.Schema({
  examNumber: { type: String, required: true },
  subjects: [
    {
      subject: { type: String, required: true },
      score: { type: Number, required: true },
    },
  ],
  resultFile: { type: String }, // stores uploaded file path/URL
});

const jambSchema = new mongoose.Schema({
  examNumber: { type: String, required: true },
  score: { type: Number, required: true },
  resultFile: { type: String }, // stores uploaded file path/URL
});

const studentSchema = new mongoose.Schema({
  // Step 1 - Personal Details
  firstName: { type: String, required: true },
  lastName: { type: String, required: true },
  otherName: { type: String },
  email: { type: String, required: true, unique: true },
  phone: { type: String },
  gender: { type: String, enum: ["male", "female", "other"] },
  dob: { type: Date },
  address: { type: String },
  passport: { type: String }, // uploaded file path/URL (via multer)

  intendedCourse: { type: String }, // plain string

  // Step 2 - Academic Details
  faculty: {
    type: mongoose.Schema.Types.ObjectId,
    ref: "Faculty",
  },
  department: {
    type: mongoose.Schema.Types.ObjectId,
    ref: "Department",
  },

  jamb: jambSchema,
  oLevel: oLevelSchema,

  // System fields
  studentId: { type: String, unique: true }, // e.g. STU/2025/1234
  enrolledAt: { type: Date, default: Date.now },
  status: {
    type: String,
    enum: ["active", "graduated", "inactive"],
    default: "active",
  },
  courses: [
    { type: mongoose.Schema.Types.ObjectId, ref: "Course", default: [] },
  ],
  fees: [
    {
      session: { type: String, required: true },
      semester: { type: String, enum: ["Harmattan", "Rain"], required: true },
      amount: { type: Number, default: 0 },
      status: {
        type: String,
        enum: ["Pending", "Paid", "Approved"],
        default: "Pending",
      },
      approvedAt: { type: Date },
    },
  ],

  password: { type: String, default: null },
});

// Virtuals for file URLs (optional but useful for frontend)
studentSchema.virtual("passportUrl").get(function () {
  return this.passport ? `/uploads/${this.passport}` : null;
});
studentSchema.virtual("jambResultUrl").get(function () {
  return this.jamb?.resultFile ? `/uploads/${this.jamb.resultFile}` : null;
});
studentSchema.virtual("oLevelResultUrl").get(function () {
  return this.oLevel?.resultFile ? `/uploads/${this.oLevel.resultFile}` : null;
});

module.exports = mongoose.model("Student", studentSchema);
